mapper.update:
update ${prefix}t_wf_id_group set
  rev_ = #{revisionNext ,jdbcType=INTEGER},
  name_ = #{name ,jdbcType=VARCHAR},
  type_ = #{type ,jdbcType=VARCHAR}
where id_ = #{id}
   and rev_ = #{revision}

mapper.select:
select g.*
from ${prefix}t_wf_id_group g, ${prefix}t_wf_id_membership membership
where g.id_ = membership.group_id_
  and membership.user_id_ = #{parameter.userId}

     and g.type_ = #{parameter.groupType}

mapper.sql:
from ${prefix}t_wf_id_group RES

  inner join ${prefix}t_wf_id_membership m on RES.id_ = m.group_id_
  inner join ${prefix}t_wf_id_user u on m.user_id_ = u.id_



    RES.id_ = #{id}


    and RES.name_ = #{name}


    and RES.name_ like #{nameLike}${wildcardEscapeClause}


    and RES.type_ = #{type}


    and u.id_ = #{userId}


    and exists (select ID_ from ${prefix}t_wf_ru_identitylink where proc_def_id_ = #{procDefId} and group_id_=RES.id_ )
