	select * from ${prefix}t_wf_ru_variable
    where task_id_ = #{parameter.taskId, jdbcType=VARCHAR}

    and (

      	name_= #{name, jdbcType=VARCHAR}

    )

select * from ${prefix}t_wf_ru_variable where
proc_inst_id_ =#{procInstId, jdbcType=VARCHAR}
