def parse_kwargs(**kwargs) -> Any:
    """解析传入的kwargs，提取tool_input和props"""
    tool_input = kwargs.get("tool_input", kwargs)
    props = kwargs.get("props", None)
    return tool_input, props


class APIToolResponse:
    """
    API工具响应数据类，用于封装返回数据结果
    """

    # 工具执行结果，执行成功返回结果数据，失败返回错误信息
    answer: Any = ""
    # 工具执行结果，执行成功返回结果数据，同 answer, 失败返回空
    block_answer: Any = ""

    def __init__(self, answer: Any = "", block_answer: Any = ""):
        self.answer = answer
        self.block_answer = block_answer

    def to_dict(self):
        """转换为字典"""
        return {"answer": self.answer, "block_answer": self.block_answer}


class ToolMapInfo(BaseModel):
    """
    工具参数映射信息模型

    用于定义工具输入参数的映射规则，支持多种映射类型：
    - fixedValue: 固定值映射
    - var: 变量映射（从变量池获取）
    - model: 选择模型映射
    - auto: 模型自动生成映射
    """

    input_name: str = Field(..., description="输入参数名称，必传字段")

    input_type: str = Field(..., description="输入参数类型，必传字段")

    map_type: Optional[Literal["fixedValue", "var", "model", "auto"]] = Field(
        None,
        description="映射类型，当有children时非必传字段。可选值：fixedValue（固定值）、var（变量）、model（选择模型）、auto（模型自动生成）",
    )

    map_value: Any = Field(
        None,
        description="映射值，当有children时非必传字段。",
    )

    enable: Optional[bool] = Field(
        None, description="是否启用此映射，当有children时非必传字段"
    )

    children: Optional[List["ToolMapInfo"]] = Field(
        default=None, description="子映射信息列表，非必传字段，用于嵌套映射场景"
    )

    def is_enabled(self) -> bool:
        """检查映射是否启用"""
        return self.enable == True or self.enable is None

    def get_map_value(self) -> Union[str, Dict[str, Any]]:
        """获取映射值"""
        return self.map_value if self.map_value is not None else ""

    def get_map_type(self) -> str:
        """获取映射类型"""
        return self.map_type if self.map_type is not None else "auto"

    class Config:
        """Pydantic配置"""

        # 允许从字典创建模型
        from_attributes = True

        # 允许额外字段
        extra = "allow"

        # 验证时使用赋值
        validate_assignment = True


